template.value:
/**
 * $DESCRIPTION$
 * @POST("/api/$ENTITY$/$ACTION$")
 * @Versions({"v1"})
 * @Transaction({
 *      @Request({}),
 *      @Response(200, body={
 *                          "data":{
 *                                  },
 *                          "count":1,
 *                          "result":"true",
 *                          "msg":"blabla..."})
 * })
 */
public function $ACTION$(){
    //验证规则
    $rules = [
/*            'FK_client_gid' => 'required|exists:client_change_log',
        'start_time'    => 'required|digits:10'*/
    ];
    $objData = $this->request->all();
    $validator = app('validator')->make($objData, $rules);
    if ($validator->fails()) return parent::ApiResponse('', 'false', 0, Util::getValidatorErrorMessage($validator));
    //Log::info('     '.__method__.'() line:'.__line__.'     $VAR = '.print_r($VAR, true));
    $END$
    return 'end';
}
